import {createSelector} from 'reselect'

const selectCart =(state)=> state.cart 

export const selectItems = createSelector(
    [selectCart],
    
    (cart)=>  cart.cartItems
)

export const selectTotalQuantity = createSelector(
    [selectItems],
    items=> console.log(items.quantity)
)
