import  cartActionType  from './cart.actionType';


const INITIAL_STATE = {
    ishidden : false
}

 const cartReducer = (state=INITIAL_STATE,action={})=>{
    switch (action.type) {
        case cartActionType.TOGGEL_CART:
            return{
                ...state,
                ishidden: !state.ishidden
            }
            
    
        default:
            return state;
    }
}

export default cartReducer;
