const cartItems=(state,action)=>{
    const arryCartItems= state.cartItems;
    const objectCartItemRecived = action.payload

    const isCartItemExists = arryCartItems.find(item=> item.id === objectCartItemRecived.id)

    console.log(isCartItemExists);

    if (isCartItemExists){
        return arryCartItems.map(item=> item.id===objectCartItemRecived.id ? ({...item,quantity:item.quantity+1}):({...item}))
    }else{
        return [...state.cartItems,{...isCartItemExists,quantity:1}]
    }


    
}
export default cartItems;
