import React from 'react';
import {connect} from 'react-redux'

import CustomeButton from '../custom-button/custom-button.component'
import {addItemToCart} from '../../redux/cart/cart.ations'
import './collection-item.styles.scss';

const CollectionItem = (props,{ id, name, price, imageUrl ,addItemToCart}) =>{
   console.log(props);
  return  (
    <div className='collection-item'>
      <div
        className='image'
        style={{
          backgroundImage: `url(${imageUrl})`
        }}
      />
        
      <div className='collection-footer'>
        <span className='name'>{name}</span>
        <span className='price'>{price}</span>
      </div>
      <span className='bt' ><CustomeButton onClick ={addItemToCart( )} >Add to Cart</CustomeButton></span>
    </div>
  );
}

const mapDispatchToProps = (dipatch)=>({
  addItemToCart : (item)=>dipatch(addItemToCart(item)) 
})

export default  connect(null,mapDispatchToProps)( CollectionItem);
