import React from 'react';
import {connect} from 'react-redux'
import {ReactComponent as ShopingBag} from '../../assets/shopping-bag.svg';
import style from './cart-icon.module.css';
import {togelCart} from '../../redux/cart/cart.ations'


const CartIcon = ({togelCart})=>{
    
    return(
        <div className={style.container}  onClick={togelCart} >
            <ShopingBag className={style.bag} />
            <span>0</span>
        </div>
    )
}

const mapDispatchToProps =(dispatch)=>({
    toggelCart : ()=>dispatch(togelCart())
})

export default connect(null,mapDispatchToProps)( CartIcon);
