import cartActionType  from './cart.actionType';

export const toggelCart =()=>({
    type: cartActionType.TOGGEL_CART
})

export const addItemToCart = (item)=>({
    type: cartActionType.ADD_ITEM_TO_CART,
    payload:item
})
