import {createSelector} from 'reselect';

const selectShop = state => state.shop

export const selectCollections = createSelector(
    [selectShop],
    (shop)=> shop.collections
)

export const selectItemCollection =(collectionName)=>createSelector(
    [selectCollections],
    (collections)=>  {  console.log(collections);  
        return collections.filter((collection)=>{  console.log(collection.routeName + '==='+ collectionName); return collection.routeName === collectionName})}
)
