import  React from 'react';
import {connect} from 'react-redux'


import {removeItemFromCart,reduceItemFromCart} from '../../redux/cart/cart.ations'
import styles from './table-row.module.css';

const TableRow =({item})=> {
    const {imageUrl,name,price,quantity} =item
        return (
            <div>
               
            
            <div className={styles.data_body} >
            <div className={styles.data_name_im} >
            <div
                className={styles.background_image}
                style={{
                    backgroundImage: `url(${imageUrl})`
                }}
                />
                <span>{name}</span>
                       
            </div>        
               
               <div className={styles.data_name } >
                   <span id={styles.sp} >{price}</span>
               </div>
               <div className={styles.data_name} >
                    <p className={styles.da}>&#10094;</p>  
                    <span id={styles.sp} >{quantity}</span>
                    <p className={styles.da} > &#10095;</p>
               </div>
               <div className={styles.data_name} >
                   <span id={styles.sp} >{price*quantity}</span>
               </div>
               <div className={styles.data_name} >
                   <span  id={styles.sp} ><p>&#10060;</p></span>
               </div>
               </div>
            </div>
        );
    
}

const mapDispatchToProps = (dipatech)=>({
reduce:(item)=>dipatech(reduceItemFromCart()),
remove:(item)=>dipatech(removeItemFromCart())
})


export default connect(null,mapDispatchToProps)( TableRow);
